              }
              r = createOutputRow(r, data.outputRowMeta.size());
              if (first) {
              first = false;
              String column_name = environmentSubstitute("pcolumn_name");
              String table_name = environmentSubstitute("table_name");
              String DriverName = environmentSubstitute("DN");
              String userName = environmentSubstitute("UN");
              String passWord = environmentSubstitute("PW");
              String URL = environmentSubstitute("URL");
              pri_column_name = column_name;
              pri_table_name = table_name;
              Class.forName(DriverName);
              conn = DriverManager.getConnection(URL, userName, passWord);
              conn.setAutoCommit(false);
              String sql = "update "+table_name+" set dataUpgradeStat = 1 where "+column_name+" = ?";
              ps = conn.prepareStatement(sql);
              }
              String tbn = get(Fields.In, "tbn").getString(r);
              String column_value = get(Fields.In, pri_column_name).getString(r);
              ps.setString(1,column_value);
              ps.addBatch();
              if(writed % 5000 == 0){
              ps.executeBatch();
              conn.commit();
              }
              writed++;
              putRow(data.outputRowMeta, r);

              return true;
              }

