/*
 * 数据接收及发送发放
 */
public boolean processRow(StepMetaInterface smi, StepDataInterface sdi) throws
KettleException {
   Object[] r = getRow();
   if (r == null) {
      setOutputDone();
      return(false);
   }
Object[] rb = createOutputRow(r, data.outputRowMeta.size());
   if (first) {
      initDic(r);
      first = false;
   }
   rb = setDic(r, rb);
   putRow(data.outputRowMeta, rb);

   return(true);
}


/*
 * 获取表中字典字段方法
 */
public Object[] setDic(Object[] r, Object[] rb) throws KettleException {
   try {
      // 获取当前转换表名
      String tbn = get(Fields.In, "tbn").getString(r);
      // 获取表中字典字段
      HashMap table_dic = (HashMap)globalDic.get(tbn);

      if (table_dic == null) {
         return(rb);
      }

      // 获取keyset获取字段

      Set      keySet      = table_dic.keySet();
      Iterator iterator    = keySet.iterator();
      HashMap  dic_dic_All = (HashMap)globalDic.get("dic_dic_All");
      while (iterator.hasNext()) {
         String  field_name = (String)iterator.next();
         String  dic_name   = (String)table_dic.get(field_name);
         Object  old_value  = get(Fields.In, field_name).getString(r);
         HashMap dic        = (HashMap)dic_dic_All.get(dic_name);
         Object  new_value  = (Object)dic.get(old_value);
         get(Fields.Out, field_name).setValue(rb, new_value);
      }
   } catch (Exception e) {
      throw new KettleException(e);
   }
   return(rb);
}


/*
 * 全局缓存字典
 */
public static HashMap globalDic = new HashMap();

/*
 * 初始化方法 将字典缓存
 */
public void initDic(Object[] r) throws KettleException{
   String            tbn             = get(Fields.In, "tbn").getString(r);
   String            url             = get(Fields.In, "url").getString(r);
   String            un              = get(Fields.In, "un").getString(r);
   String            pw              = get(Fields.In, "pw").getString(r);
   String            dn              = get(Fields.In, "dn").getString(r);
   PreparedStatement pst             = null;
   ResultSet         rs              = null;
   PreparedStatement pst2            = null;
   ResultSet         rs2             = null;
   Connection        dbconConnection = null;

   try {
      Class.forName(dn);
      dbconConnection = DriverManager.getConnection(url, un, pw);
      //缓存表-字段 字典
      String table_field_sql = "select field_name,dic_name from table_field where table_name = ? ";

      pst = dbconConnection.prepareStatement(table_field_sql);
      pst.setString(1, tbn);
      rs = pst.executeQuery();
      HashMap table_dic = new HashMap();
      globalDic.put(tbn, table_dic);
      while (rs.next()) {
         String field_name = rs.getString(1);
         String dic_name   = rs.getString(2);
         table_dic.put(field_name, dic_name);
      }
      //缓存新老字典对照字典

      String dic_dic_sql = "select a.dic_name as dic_name , a.old_value as old_value,a.new_value as new_value from dic_dic a , table_field b where a.dic_name = b.dic_name and b.table_name = ? ";
      pst2 = dbconConnection.prepareStatement(dic_dic_sql);
      pst2.setString(1, tbn);
      rs2 = pst2.executeQuery();
      HashMap dic_dic_All = new HashMap();
      globalDic.put("dic_dic_All", dic_dic_All);
      while (rs2.next()) {
         String  dic_name  = rs2.getString(1);
         String  old_value = rs2.getString(2);
         String  new_value = rs2.getString(3);
         HashMap dic_dic   = null;
         if (dic_dic_All.get(dic_name) == null) {
            dic_dic = new HashMap();
            dic_dic_All.put(dic_name, dic_dic);
         } else {
            dic_dic = (HashMap)dic_dic_All.get(dic_name);
         }
         dic_dic.put(old_value, new_value);
      }
   } catch (Exception e) {
      // TODO Auto-generated catch block
   }finally {
      if (dbconConnection != null) {
         dbconConnection.close();
      }
      if (rs != null) {
         rs.close();
      }
      if (pst != null) {
         pst.close();
      }
      if (rs2 != null) {
         rs2.close();
      }
      if (pst2 != null) {
         pst2.close();
      }
   }
}

import java.sql.*;
public boolean processRow(StepMetaInterface smi, StepDataInterface sdi) throws KettleException {
   if (first) {
      first = false;
   }

   Object[] r = getRow();

   if (r == null) {
      setOutputDone();
      return(false);
   }

   r = createOutputRow(r, data.outputRowMeta.size());
   String            tbn             = get(Fields.In, "tbn").getString(r);
   String            url             = get(Fields.In, "url").getString(r);
   String            un              = get(Fields.In, "un").getString(r);
   String            pw              = get(Fields.In, "pw").getString(r);
   String            dn              = get(Fields.In, "dn").getString(r);
   PreparedStatement pst             = null;
   ResultSet         rs              = null;
   PreparedStatement pst2            = null;
   PreparedStatement pst3            = null;
   Connection        dbconConnection = null;
   //alter table DATA_EXTRACTION add dataUpgradeStat number default 0;
   //comment on column DATA_EXTRACTION.dataUpgradeStat is '当前数据是否被抽取，0未抽，1已抽。默认0';
   try {
      Class.forName(dn);
      dbconConnection = DriverManager.getConnection(url, un, pw);
      String isColumn = "SELECT TABLE_NAME as TABLE_NAME FROM USER_TAB_COLUMNS WHERE TABLE_NAME = ? AND COLUMN_NAME = 'dataUpgradeStat'";
      String addColumn = "alter table ? add dataUpgradeStat number default 0";
      String addComment = "comment on column ?.dataUpgradeStat is '当前数据是否被抽取，0未抽，1已抽。默认0'";
      pst = dbconConnection.prepareStatement(isColumn);
      pst.setString(1, tbn);
      rs = pst.executeQuery();
      if (rs.next()) {
         pst2 = dbconConnection.prepareStatement(addColumn);
         pst2.setString(1, tbn);
         pst2.executeUpdate();
         pst3 = dbconConnection.prepareStatement(addComment);
         pst3.setString(1, tbn);
         pst3.executeUpdate();

      }
   } catch (Exception e) {
      // TODO Auto-generated catch block
   }finally {
      if (dbconConnection != null) {
         dbconConnection.close();
      }
      if (rs != null) {
         rs.close();
      }
      if (pst != null) {
         pst.close();
      }
      if (pst2 != null) {
         pst2.close();
      }
      if (pst3 != null) {
         pst3.close();
      }
   }


   putRow(data.outputRowMeta, r);

   return(true);
}

